mapper.update:
update setmeal
name = #{name}, category_id = #{categoryId}, price = #{price}, status = #{status}, description = #{description}, image = #{image}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

mapper.select:
select s.*, c.name as categoryName
from setmeal as s
left join category as c on s.category_id = c.id


        and s.name like concat('%', #{name}, '%')


        and s.category_id = #{categoryId}


        and s.status = #{status}


order by s.update_time desc
LIMIT #{offset}, #{pageSize}

select status
from setmeal where id in

    #{id}
